mapper.select:
SELECT DISTINCT tb_category.category_name,tb_board.board_id, tb_board.title, tb_board.user_name,tb_board.views,tb_board.created_at, tb_board.edited_at, tb_file.board_id as boardIdInFile FROM tb_board LEFT JOIN tb_category ON tb_board.category_id = tb_category.category_id LEFT JOIN tb_file ON tb_board.board_id = tb_file.board_id WHERE tb_board.created_at BETWEEN #{startDate} AND #{endDate}
AND (title LIKE CONCAT('%',#{searchText},'%') OR user_name LIKE CONCAT('%',#{searchText},'%') OR content LIKE CONCAT('%',#{searchText},'%') )
AND tb_board.category_id = #{categoryId}
ORDER BY tb_board.created_at DESC LIMIT #{pageSize} OFFSET #{startRow}

SELECT (
SELECT COUNT(*)
FROM tb_board
WHERE created_at BETWEEN #{startDate} AND #{endDate}

    AND (title LIKE CONCAT('%',#{searchText},'%')
    OR user_name LIKE CONCAT('%',#{searchText},'%')
    OR content LIKE CONCAT('%',#{searchText},'%')
    )


    AND category_id = #{categoryId}

) AS row_count

mapper.update:
UPDATE tb_board
SET views = views + 1
WHERE board_id = #{boardId}

UPDATE tb_board
SET title = #{title}, user_name = #{userName}, content = #{content}, edited_at = current_timestamp
WHERE board_id = #{boardId}
